package main

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

var floorTests = []struct {
	input          string
	secondPart     bool
	expectedFirst  int
	expectedSecond int
}{
	{"(())", false, 0, -1},
	{"(((", false, 3, -1},
	{"))(((((", false, 3, -1},
	{"())", false, -1, -1},
	{")))", false, -3, -1},
	{")", true, -1, 1},
	{"()())", true, -1, 5},
}

func Test_FindFloor_SampleCases(t *testing.T) {
	for _, tt := range floorTests {
		actual, actualBasement := findFloor(tt.input, tt.secondPart)
		assert.Equal(t, tt.expectedFirst, actual)
		assert.Equal(t, tt.expectedSecond, actualBasement)
	}
}

