package main

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

var floorTests = []struct {
	input    string
	expected int
}{
	{"(())", 0},
	{"(((", 3},
	{"))(((((", 3},
	{"())", -1},
	{")))", -3},
}

func Test_FindFloor_SampleCases(t *testing.T) {
	for _, tt := range floorTests {
		actual := findFloor(tt.input)
		assert.Equal(t, tt.expected, actual)
	}
}

